import React from 'react'
import { getUser } from "../../services/auth"
import api from "../../services/api"
import ReactCrop from "react-image-crop"
import FormLogoImageCrop from "./FormLogoImageCrop"

class FormLogo extends React.Component {
    constructor(){
        super()
        this.state = {
            logoFile: null,
            userId: null,
            src: null,
            crop: {
                unit: "%",
                width: 100,
                aspect: 16 / 7
            }
        }
        this.handleChange = this.handleChange.bind(this)
    }

    componentDidMount(){
        this.setState({userId: getUser()})
    }

    handleChange(event) {
        const {name, value, type, checked} = event.target
        type === "checkbox" ? this.setState({ [name]: checked }) : this.setState({ [name]: value })
    }

    handleSubmit = async e => {

        e.preventDefault();
        const { logoFile, userId } = this.state

        console.log(this.state)

        if (!logoFile || !userId) {
            this.setState({ error: "Suba sua imagem de Logo para salvar" })
        } else {
            try {
                const response = await api.post("/auth//api/users/logo", { logoFile, userId })

                console.log(response)

                //login(responseLogin.data.access_token)
                //this.props.history.push("/")

            } catch (err) {
                this.setState({
                error:
                    "Houve um problema com o login, verifique suas credenciais. T.T"
                })
            }
        }

    }


    render(){
        const { crop, logoFile, src } = this.state;

        return (
            <div className="panel">
                <div className="panel-heading">
                    <h3 className="panel-title">Logo</h3>
                </div>
                <div className="panel-body container-fluid">
                    <form onSubmit={this.handleSubmit}>    
                        <input type="file" onChange={this.onSelectFile} />
                        <div className="row">
                            <div className="CropComponent" style={{width: "100%"}}>
                                {src && (
                                    <ReactCrop
                                        src={src}
                                        crop={crop}
                                        onImageLoaded={this.onImageLoaded}
                                        onComplete={this.onCropComplete}
                                        onChange={this.onCropChange}
                                    />
                                )}
                                <br/>
                                {logoFile && (
                                <img alt="Crop" name="logoFile" id="logoFile" src={logoFile} />
                                )}
                            </div>
                        </div>
                        <button type="submit" className="btn btn-success mt-20">Salvar</button>
                    </form>
                </div>
            </div>
        )
    }
}

export default FormLogo
