import React from 'react'
import api from "../../services/api";

class FormCategories extends React.Component {
    constructor(){
        super()
        this.state = {
            isLoading: true,
            categoriesPrimary: [],
            categoriesSecondary: [],
        }
        this.handleChange = this.handleChange.bind(this)
    }

    handleChange(event) {
        const {name, value, type, checked} = event.target
        type === "checkbox" ? this.setState({ [name]: checked }) : this.setState({ [name]: value })
    }

    componentDidMount(){
        const response = api.post("/api/postcategories", {  })

        this.setState({categoriesPrimary: response})

        fetch("http://gerarme-api.test/api/postcategories") 
            .then(response => response.json())
            .then(data => {
                this.setState({isLoading: false, categoriesPrimary: data, categoriesSecondary: data})
            })
    }

    render(){

        const availableCategoriesPrimary = this.state.categoriesPrimary.map((cat) => 
                <option key={cat.id} value={cat.id} >{cat.name}</option>
            );
        const availableCategoriesSecondary = this.state.categoriesSecondary.map((cat) => 
                <option key={cat.id} value={cat.id}>{cat.name}</option>
            );

        return(
            <div className="panel">
                <div className="panel-heading">
                    <h3 className="panel-title">Categorias</h3>
                </div>
                <div className="panel-body container-fluid">
                    <form method="POST" action="http://my.gerar.me/user/configs_categories">                      
                        <div className="row row-lg">
                            <div className="col-lg-12">
                                <label>Categoria #1 <small>Obrigatória</small></label>
                                <select name="category1" className="form-control" aria-hidden="true" required>
                                    <option key="" value=""></option>
                                    {availableCategoriesPrimary}
                                </select>
                            </div>
                            <div className="col-lg-12" style={{marginTop: 15}}>
                                <label>Categoria #2 <small>Opcional</small></label>
                                <select name="category2" className="form-control" aria-hidden="true">
                                    <option key="" value=""></option>
                                    {availableCategoriesSecondary}
                                </select>
                            </div>
                            <div className="col-lg-12">
                                <span className="text-help">Caso não encontre uma categoria para seu negócio, solicite a criação por <a style={{cursor: 'pointer'}} data-target="#exampleFormModal" data-toggle="modal"><u>aqui</u></a>.</span>
                            </div>
                        </div> 
                        <input type="submit" className="btn btn-success mt-20" value="Salvar" />
                    </form>
                </div>
            </div>
        )
    }
}

export default FormCategories
