import React from "react"
import { BrowserRouter as Router, Route, Switch, Redirect } from 'react-router-dom'
import HomePage from './components/home/HomePage'
import ConfigsPage from './components/configs/ConfigsPage'
import PostsPage from './components/posts/PostsPage'
import Login from './components/user/Login'

import RouteLayout from "./components/layout/RouteLayout"

import { isAuthenticated } from "./services/auth"

const PrivateRoute = ({ component: Component, ...rest }) => (
    <Route {...rest} render={matchProps => (
        isAuthenticated() ? (
            <AppLayout>
                <Component {...matchProps} />
            </AppLayout>
        ) : (
            <Redirect to={{ pathname: "/", state: { from: matchProps.location } }} />
        )


        )} 
    />
);

const Routes = () => (
    <Router>
        <div id="wrapper">
        <Switch>
            <RouteLayout key="home" exact path="/" component={HomePage}/>
            <RouteLayout key="posts" path="/posts" component={PostsPage}/>
            <RouteLayout key="configs" path="/configs" component={ConfigsPage}/>
            <Route key="login" path="/login" component={Login}/>
        </Switch>
        </div>
    </Router>
);

export default Routes
