import React from 'react'
import FormLogoImageCrop from './FormLogoImageCrop'

class FormLogo extends React.Component {
    constructor(){
        super()
        this.state = {
            logoImage: ""
        }
        this.handleChange = this.handleChange.bind(this)
    }

    handleChange(event) {
        const {name, value, type, checked} = event.target
        type === "checkbox" ? this.setState({ [name]: checked }) : this.setState({ [name]: value })
    }

    render(){
        return (
            <div className="panel">
                <div className="panel-heading">
                    <h3 className="panel-title">Logo</h3>
                </div>
                <div className="panel-body container-fluid">
                    <form method="POST" action="http://my.gerar.me/user/configs_logo">    
                        <ReactCrop/>                    
                        <div className="row row-lg">
                            <div className="col-lg-12">
                                <input name="logoImage" type="file" onChange={this.handleChange}></input>
                                <span className="text-help">Sugerimos logos com proporções retangulares. (225px x 60px)</span>
                            </div>
                        </div>
                        <button type="submit" className="btn btn-success mt-20">Salvar</button>
                    </form>
                    <p>{this.state.logoImage}</p>
                </div>
            </div>
        )
    }
}

export default FormLogo
