import React from 'react'
import PostCard from '../posts/PostCard'
import { getToken } from "../../services/auth";

class PostPage extends React.Component {
    constructor(){
        super()
        this.state = {
            isLoading: true,
            posts: []
        }
    }

    componentDidMount(){
        fetch("http://gerarme-api.test/api/posts", {
                    method: 'GET',
                    headers: {
                    'Authorization': 'Bearer' + getToken()
                    }
                }) 
            .then(response => response.json())
            .then(data => {
                this.setState({isLoading: false, posts: data})
            })
    }

    render(){
        const aposts = this.state.posts.map((postItem) => 
        <PostCard key={postItem.id} value={postItem.id} post={postItem} />
            );
        return (
            <div>
                <div className="page-header page-header-bordered page-header-tabs">
                    <h1 className="page-title">Posts</h1>
                    <ul className="nav nav-tabs nav-tabs-line" role="tablist" id="exampleFilter">
                        <li className="nav-item" role="presentation">
                            <a className="active nav-link" href="/" aria-controls="exampleList" aria-expanded="true" role="tab" data-filter="*">Todos</a>
                        </li>
                        <li className="nav-item" role="presentation">
                            <a className="nav-link" href="#" aria-expanded="false" role="tab" data-filter="Feed">Feed</a>
                        </li>
                        <li className="nav-item" role="presentation">
                            <a className="nav-link" href="#" aria-expanded="false" role="tab" data-filter="Stories">Stories</a>
                        </li>
                    </ul>
                </div>
                <div className="page-content">
                    <ul className="blocks blocks-100 blocks-xxl-4 blocks-lg-4 blocks-md-3 blocks-sm-2" data-plugin="filterable" data-filters="#exampleFilter" style={{position: "relative"}}>    
                        {aposts}
                    </ul>
                </div>
            </div>
        )
    }
}

export default PostPage
