import React from 'react'
import FormLogoImageCrop from './FormLogoImageCrop'

class FormLogo extends React.Component {
    constructor(){
        super()
        this.state = {
            logoFile: null
        }
        this.handleChange = this.handleChange.bind(this)
    }

    handleChange(event) {
        const {name, value, type, checked} = event.target
        type === "checkbox" ? this.setState({ [name]: checked }) : this.setState({ [name]: value })
    }

    handleSubmit = async e => {

        e.preventDefault();
        const { logoFile } = this.state

        if (!logoFile) {
            this.setState({ error: "Suba sua imagem de Logo para salvar" })
        } else {
            try {
                const response = await api.post("/auth//api/users/logo", { logoFile })

                //login(responseLogin.data.access_token)
                //this.props.history.push("/")

            } catch (err) {
                this.setState({
                error:
                    "Houve um problema com o login, verifique suas credenciais. T.T"
                })
            }
        }

    }

    render(){
        return (
            <div className="panel">
                <div className="panel-heading">
                    <h3 className="panel-title">Logo</h3>
                </div>
                <div className="panel-body container-fluid">
                    <form onSubmit={this.handleSubmit}>    
                        <FormLogoImageCrop src=""/> 
                        <button type="submit" className="btn btn-success mt-20">Salvar</button>
                    </form>
                    <p>{this.state.logoImage}</p>
                </div>
            </div>
        )
    }
}

export default FormLogo
