import React from 'react'

class FormLogo extends React.Component {
    constructor(){
        super()
        this.state = {
            logoImage: ""
        }
        this.handleChange = this.handleChange.bind(this)
    }

    handleChange(event) {
        const {name, value} = event.target
        this.setState({
            [name]: value
        })
    }

    render(){
        return (
            <div class="col-sm-12 col-md-12 col-lg-4">   
                <div class="panel">
                    <div class="panel-heading">
                        <h3 class="panel-title">Logo</h3>
                    </div>
                    <div class="panel-body container-fluid">
                        <form method="POST" action="http://my.gerar.me/user/configs_logo" enctype="multipart/form-data">                        
                            <div class="row row-lg">
                                <div class="col-lg-12">
                                    <input name="logoImage" type="file" onChange={handleChange()}></input>
                                    <span class="text-help">Sugerimos logos com proporções retangulares. (225px x 60px)</span>
                                </div>
                            </div>
                            <button type="submit" class="btn btn-success mt-20">Salvar</button>
                        </form>
                        <p>{this.state.logoImage}</p>
                    </div>
                </div>
            </div>
        )
    }
}

export default FormLogo
