import React from 'react'
import '../../assets/css/login.css'

import api from "../../services/api";
import { login, getToken } from "../../services/auth";

class ResetPassword extends React.Component {
    constructor(){
        super()
        this.state = {
            email: ''
        }
        this.handleChange = this.handleChange.bind(this)
    }

    handleChange(event) {
        const {name, value, type, checked} = event.target
        type === "checkbox" ? this.setState({ [name]: checked }) : this.setState({ [name]: value })
    }

    handleSignIn = async e => {

        e.preventDefault();
        const { email } = this.state;

        if (!email) {
            this.setState({ error: "Preencha e-mail para continuar!" });
        } else {
            try {
                const response = await api.post("/auth/login", { email });
                login(response.data.access_token);
                console.log(getToken())
                this.props.history.push("/");
            } catch (err) {
                this.setState({
                error:
                    "Houve um problema com o login, verifique suas credenciais. T.T"
                });
            }
        }
    };

    render(){
        return (
            <div className="page vertical-align text-center" data-animsition-in="fade-in" data-animsition-out="fade-out">
                <div class="page-content vertical-align-middle animation-slide-top animation-duration-1">
                    <h2>Esqueceu a Senha?</h2>
                    <p>Informe seu e-mail de cadastro</p>

                    <form method="POST" action="http://my.gerar.me/password/email" _lpchecked="1">
                        <div class="form-group row">
                            <input id="email" type="email" class="form-control " name="email" value="" required="" autocomplete="email" autofocus=""/>
                        </div>
                        <div class="form-group row mb-0">
                            <button type="submit" class="btn btn-primary btn-block">
                                Enviar Solicitação de Nova Senha
                            </button>
                        </div>
                    </form>

                    <footer class="page-copyright">
                        <p>Gerar.me</p>
                        <p>© 2019. Todos os Direitos Reservados.</p>
                    </footer>
                </div>
            </div>
        )
    }
}

export default ResetPassword
