import React from 'react'
import AppLoading from './layout/AppLoading'
import AppPageheader from './layout/AppPageheader'

class Configs extends React.Component{
    constructor(){
        super()
        this.state = {
            isLoading: true,
            configs: {}
        }

        //Todos as functions de eventos devem ser feitos binds no constructor
        this.handleSaveLogo = this.handleSaveLogo.bind(this)
    }

    componentDidMount(){
        fetch("http://gerarme-api.test/api/post/1/") 
            .then(response => response.json())
            .then(data => {
                this.setState({isLoading: false, configs: data})
            })

        //this.setState({isLoading: false})
    }

    handleSaveLogo(){
        //Inverte isLoadingState
        //Exemplo - Handler de Evento
        this.setState(prevState => {
            return {
                isLoading: !prevState.isLoading
            }
        })
    }

    render(){
        return (
            <div>
                <AppPageheader header="Configure seu Negócio" detail="Precisamos saber mais sobre seu negócio" />
                <div className="page-content container-fluid">
                    <p>Conteúdo Página de Configurações</p>
                </div>
            </div>
        )
    }
}

export default Configs
